- (void)viewDidDisappear:(BOOL)animated
{
	[super viewDidDisappear:animated];
}

- (BOOL)shouldAutorotateToInterfaceOrientation:(UIInterfaceOrientation)interfaceOrientation
{
    // Return YES for supported orientations
    if ([[UIDevice currentDevice] userInterfaceIdiom] == UIUserInterfaceIdiomPhone) {
        return (interfaceOrientation != UIInterfaceOrientationPortraitUpsideDown);
    } else {
        return YES;
    }
}

- (NSInteger)numberOfRowsInCustomScrollView:(CustomScrollView *)scrollView
{
    return 100;
}

- (BOOL)customScrollView:(CustomScrollView *)scrollView ClickOnRow:(NSInteger) row Url:(NSURL *) url
{
    UIAlertView *alert = [[UIAlertView alloc] initWithTitle:@"Hyperlink Clicked!" message:[NSString stringWithFormat:@"Row Index: %d \nUrl: %@",row,url] delegate:self cancelButtonTitle:@"Cancel" otherButtonTitles:nil];
    [alert show];
    NSLog(@"%d %@",row,url);
    return  NO;
}

